import classes from './CartItem.module.css';

const CartItem = (props) => {
  const { title, quantity, total, price } = props.item;

  // total과 price가 유효한지 확인 후 toFixed를 호출
  const formattedTotal = total !== undefined ? total.toFixed(2) : 'N/A';
  const formattedPrice = price !== undefined ? price.toFixed(2) : 'N/A';

  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${formattedTotal}{' '}
          <span className={classes.itemprice}>({formattedPrice}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button>-</button>
          <button>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
